        foreach (var tag in tags)
        {
            tagDetails.Add(new KeyValuePair<string, string>(tag.Title, tag.Title));
        }

        if (!string.IsNullOrEmpty(id))
        {
            var blogDetail = await this.blogService.GetBlogById(id);
